import './content.css'
import {MoreHoriz, ThumbUpOutlined, ChatBubbleOutline} from '@material-ui/icons'
import {Users} from '../../dummyData'
import { useState, useReducer} from 'react';

export default function Content({content}) {
    // const [like, setLike] = useState(content.like);
    // const [disLike, setDislike] = useState(false);

    // const likeHandler=()=>{
    //     setLike(disLike ? like-1:like+1)
    //     setDislike(!disLike)



        const initialState = {
        likes: content.like,
        }

        const appReducer = (state, action) => {
        switch(action.type) {
            case 'HANDLE_LIKE':
            return {
                ...state,
                likes: state.likes + action.payload
            }
            case 'HANDLE_DISLIKE':
            return {
                ...state,
                dislikes: state.dislikes + action.payload
            }
            default:
            return state
        }
        }


        const [state, dispatch] = useReducer(appReducer, initialState)
        const { likes } = state
        const [status, setStatus] = useState(null)

        const likeHandler = () => {
            if (status==='like') {
            setStatus(null)
            dispatch({
                type: 'HANDLE_LIKE',
                payload: -1,
            })
            } else {
            setStatus('like')
            if (status==='dislike') {
                dispatch({
                type: 'HANDLE_DISLIKE',
                payload: -1,
                })
            }
            dispatch({
                type: 'HANDLE_LIKE',
                payload: 1,
            })
            }
        }

    return (
        <div className="contentContainer">
            <div className="contentWrapper">
                <div className="contentTop">
                    <div className="contentLeft">
                        <img src={Users.filter((u)=>u.id===content?.userId)[0].profilePicture} alt="" className="leftProfile" />
                        <span className="leftUser">{Users.filter((u)=>u.id===content?.userId)[0].username}</span>
                        <span className="leftDate">{content.date}</span>
                    </div>

                    <div className="contentRight">
                        <MoreHoriz className="moreIcon" htmlColor="black"/>

                    </div>
                </div>
                <div className="contentCenter">
                    <span className="centerText">{content?.desc}</span>
                    <img src={content.photo} alt="" className="centerImg" />

                </div>
                <div className="contentBottom">
                    <div className="bottomLeft">
                        <img className="likeIcon" src="/assets/like.png" alt="" />
                        {/* <img className="likeIcon" src="/assets/heart.png" alt="" /> */}
                        <span className="likeCounter">{likes}</span>
                    </div>
                    <div className="bottomRight">
                        <span className="commentText">{content.comment} Komentar</span>

                    </div>
                </div>
                <div className="contentAction">
                    <div className="likeAction">
                        <div className="lAction" onClick={likeHandler}>
                        <ThumbUpOutlined className={status==='like'? 'btn active' : 'btn'} />
                        <span className="lText"></span>
                        </div>
                    </div>
                    <div className="commentAction">
                        <div className="cAction">
                        <ChatBubbleOutline className="cIcon"/>
                        <span className="cText">Komentar</span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
